import React, { Component } from 'react'
import Header from './Header'
export default class About extends Component {
    state = {
        loadin: true,
        list: []
        
    }
    async componentDidMount(){
        const url= "http://127.0.0.1:5000/about";
        const response = await fetch(url);
        const data =await response.json();
        this.setState({list: data.data, loadin: false});
        console.log(data.data)
        
    }
    render() {
        return (
            
           <div>
                <Header/>
                <div id="ja-mainbody" >
                    <div id="ja-main" >
                        <div className="inner clearfix">
                            <div id="system-message-container"></div>
                        <div id="ja-contentwrap" className="clearfix">
                            <div id="ja-content" className="column" >
                                <div id="ja-current-content" className="column">
                                    <div id="ja-content-main" className="ja-content-main clearfix">
                                        <div className="item-page clearfix">
                                            <h2 className="contentheading">
                                                <a>БИДНИЙ ТУХАЙ</a>
                                            </h2>
                                            <p>
                                            {this.state.list.map(li => (
                <p className="ab">{li.desc}</p>
                ))}
                                            <span>
                                                <img className="img-border" src="/img/about.jpg">
                                                   {li.desc} 
                                                </img>

                                            </span>
                                            </p>
                                            <p>
                                                <span>
                                                    {li.desc}
                                                </span>
                                            </p>
                                        </div>

                                    </div>

                                </div>

                            </div>

                        </div>
                        </div>
                    </div>
               
            </div>
           </div>
               
               
        )
    }
}
