import React, { useState } from 'react'
import { useHistory, useLocation} from "react-router-dom";

export default function(props){console.log('props', props)
let history = useHistory(); console.log(history)
  let location = useLocation(); console.log(location)

let [email, setEmail] = useState('')
let [pass, setPass] = useState('')

function burtguuleh(){
    console.log('aaa=>' , email, pass)
    let req = new XMLHttpRequest();
            req.open('POST', 'https://identitytoolkit.googleapis.com/v1/accounts:signUp?key=AIzaSyD45M3YyfFFAjfjQrgihirerxrlAaDx3jg');
            req.setRequestHeader('Content-Type', 'application/json');
            req.responseType = 'json'
            req.onload = function () {
                console.log('res', req.response);
                props.setUser({
                    email: req.response.email,
                    token: req.response.idToken,
                    id: req.response.localId
                })
                if(location.state)
                history.push(location.state.from);
                else history.push('')
            };
            req.send(JSON.stringify({email: email, password:pass, returnSecureToken: true}));
}

function nevtreh(){
    console.log('aaa=>' , email, pass)
    let req = new XMLHttpRequest();
    req.open('POST', 'https://identitytoolkit.googleapis.com/v1/accounts:signInWithPassword?key=AIzaSyD45M3YyfFFAjfjQrgihirerxrlAaDx3jg');
    req.setRequestHeader('Content-Type', 'application/json');
    req.responseType = 'json'
    req.onload = function () {
        console.log('res', req.response);
        props.setUser({
            email: req.response.email,
            token: req.response.idToken,
            id: req.response.localId
        })
        if(location.state)
        history.push(location.state.from);
        else history.push('')
    };
    req.send(JSON.stringify({email: email, password:pass, returnSecureToken: true}));
}

    if(props.user) return(<div>HOme</div>
        
        )
    else return(
    <div className="form-signin" >
    <img className="mb-4" src="../assets/brand/bootstrap-solid.svg" alt="" width="72" height="72"/>
    <h1 className="h3 mb-3 font-weight-normal">Бүртгүүлэх</h1>
    <input type="email"  className="form-control" placeholder="Email address" required autoFocus onChange={e=> setEmail(e.target.value)}/>
    <input type="password" className="form-control" placeholder="Password" required onChange={e=> setPass(e.target.value)}/>
    <button className="btn btn-lg btn-primary btn-block" onClick={burtguuleh}>Бүртгүүлэх</button>
    <button className="btn btn-lg btn-primary btn-block" onClick={nevtreh}>Нэвтрэх</button>
        </div>
        )
}
