import React, { Component } from 'react'
import Header from './Header'

export default class Event extends Component {
    state = {
        loadin: true,
        list: []
        
    }
    async componentDidMount(){
        const url= "http://127.0.0.1:5000/event";
        const response = await fetch(url);
        const data =await response.json();
        this.setState({list: data.data, loadin: false});
        console.log(data.data)
        
    }
    render() {
        if (this.state.loadin){
            return <div>loading</div>;
         }
         if (!this.state.list.length){
             return <div>didn get menu list</div>;
         }
        return (
            
            <div>
                <Header/>
                <div className="section ng-scope" ng-controller="EventListCtrl">
                    <div className="uk-container uk-container-center">
                        <h3 className="letter-space-1 uk-text-uppercase">Арга хэмжээ</h3>
                        <div className="uk-grid">
                        {this.state.list.map(li => (
                            <div className="uk-width-large-5-6 uk-width-medium-5-6 uk-row-first">
                                <div className="uk-grid">
                                    <div className="uk-width-large-1-3 uk-width-medium-1-2 ng-scope">
                                        <a>
                                            <p>
                                                <img src="/img/event1.jpg"></img>
                                            </p>
                                            <p className="text-18 letter-space-1 uk-text-uppercase uk-text-bold uk-margin-remove ng-binding">
                                                {li.name}
                                            </p>
                                        </a>
                                        <p className="ng-binding">
                                            <p>{li.sub}</p>
                                        </p>
                                    </div>
                                </div>
                            </div>
                        ))}
                        </div>
                    </div>
                </div>
            </div>
        )
    }
}
