import React, { Component } from 'react'
import Header from './Header'
export default class About extends Component {
    state = {
        loadin: true,
        list: []
        
    }
    async componentDidMount(){
        const url= "http://127.0.0.1:5000/about";
        const response = await fetch(url);
        const data =await response.json();
        this.setState({list: data.data, loadin: false});
        console.log(data.data)
        
    }
    render() {
        return (
            <div >
                <Header/>
                <h3 className="about">Бидний тухай</h3>
                <img className="ab" src="img/furniture-store.jpg" style={{width:"700px"}}></img>
                {this.state.list.map(li => (
                    <div>
                        <p className="ab">{li.desc}</p>
                    </div>
                ))}
                
            </div>
        )
    }
}
