import React,{useState,useEffect} from 'react';

import { Link } from 'react-router-dom';
import {HeaderTop} from './HeaderTop.js';
import { Login } from './Login.js';
import { Firebase} from '/.Firebase.js';
import {
  BrowserRouter as Router,
  Switch,
  Route} from "react-router-dom"; 
import fire from './Firebase.js';

export function App(props) {
  const [user,setUser] = useState('');
  const [email,setEmail] = useState('');
  const [password,setPassword] = useState('');
  const [emailError,setEmailError] = useState('');
  const [passwordError,setPasswordError] = useState('');
  const [hasAccount,setHasAccount] = useState('');

  const clearInputs = () => {
    setEmail('');
    setPassword('');
  }
  const clearErrors = () => {
    setEmailError('');
    setPasswordError('');
  }
  const handleLogin = () => {
    clearErrors();
    Login 
    .auth()
    .signInWithEmailAndPassword(email,password)
    .catch(err => {
      switch(err.code){
      case "auth/invalid-email":
      case "auth/user-disabled":
        case "auth/user-not-found":
          setEmailError(err.message);
          break;
        case "auth/wrong-password":
          setPasswordError(err.message);
          break;
        }
      });
  };
  const handleSignup = () => {
    clearErrors();
    Login 
    .auth()
    .createUserWithEmailAndPassword(email,password)
    .catch(err => {
      switch(err.code){
      case "auth/email-already-in-use":
      case "auth/invalid-email":
        setEmailError(err.message);
        break;
      case "auth/weak-password":
        setPasswordError(err.message);
        break;
    }
  });
  };
  const handleLogout = () => {
    fire.auth().signOut;
  };
  const authListener = () => {
    fire.auth().onAuthStateChanged(user => {
      if(user) {
        clearInputs();
        setUser(user);
      } else {
        setUser("");
      }
    });
  };
  useEffect(() => {
    authListener();
  },[]);
  return (
      <Router>
        <div>
          <Switch>
            <Route exact path="/">
              <HeaderTop />
            </Route>
            <Route path="/Login">
              <Login email={email} setEmail={setEmail} password={password} setPassword={setPassword} handleLogin={handleLogin} handleSignup={handleSignup} hasAccount={hasAccount} setHasAccount={setHasAccount} emailError={emailError} passwordError={passwordError} />
            </Route>
          </Switch>
        </div>
      </Router>

  );
}
